import React, { Component } from 'react';
import { SafeAreaView, View, FlatList, StyleSheet, Text, StatusBar } from 'react-native';
import PropoTypes from 'prop-types';
import data from '../../resources/data.json';
import styles from './styles';
import Toolbar from '../../components/Toolbar';

const boardData = data.boards;
console.log(boardData.map((i => ({name: i.name, id: i.id, thumbnailPhoto: i.thumbnailPhoto }))));



const Item = ({boardData}) => (
  <View style={styles.item}>
    <Text style={styles.title}>{boardData}</Text>
  </View>
);

const Board = () => {
  const renderBoardItems = ({ item }) => (
    <Item name={item.name} />
  );

  return (
    <SafeAreaView style={styles.container}>
      <FlatList
        data={data.boards}
        renderItem={renderBoardItems}
        keyExtractor={(item) => item.id}
      />
    </SafeAreaView>
  );
}

export default Board;
